mapper.update:
update board
   set boa_status = 'N'
 where boa_no = #{bno}

mapper.insert:
	insert into 
		board
		    (
	    	boa_no
		    ,mem_no
		    ,boa_category
		    ,boa_title
		    ,boa_content
		    ,boa_enroll_date
		    ,boa_img_path
		    )
	    values
    		(
		    seq_bno.nextval
		    ,#{mno}
		    ,#{category}
		    ,#{title}
		    ,#{content}
		    ,sysdate
		    ,#{imgPath}
		    )

mapper.update:
update 
	board
	set 
		boa_category = #{category}
	    , boa_title =#{title}
	    , boa_content=#{content}
	    , boa_img_path =#{imgPath}
	    , boa_update_date = sysdate
	where 
		boa_no = #{bno}
	and boa_status = 'Y'

mapper.insert:
		insert into 
