mapper.insert:
insert
  into office
  (
  	   office_no
  	 , branch
  	 , address
  	 , manager
  	 , facility
  	 , type_name
  	 , person
  	 , price
  	 , off_img_path
  )
values
  (
  	   SEQ_FNO.nextval
  	 , #{branch}
  	 , #{address}
  	 , #{manager}
  	 , #{facility}
  	 , #{typeName}
  	 , #{person}
  	 , #{price}
  	 , #{offImgPath}
  )

mapper.update:
update
	   office
  set  branch = #{branch}
  	 , address = #{address}
  	 , manager = #{manager}
  	 , facility = #{facility}
  	 , type_name = #{typeName}
  	 , person = #{person}
  	 , price = #{price}
  	 , off_img_path = #{offImgPath}
 where office_no = #{officeNo}

update
	   attachment
  set  file_path = #{filePath}
where  file_no = #{fileNo}
  and  ref_fno = #{refFno}
  and  ref_bno = 9
  and  status = 'Y'

mapper.insert:
insert
  into booking
  (
  	   bookin_no
  	 , office_no
  	 , mem_no
  	 , start_date
  	 , end_date
  	 , request_date
  	 , modify_date
  	 , status
  	 , person
  	 , phone
  	 , b_ask
  	 , total
  )
values
  (
  	   seq_bno.nextval
  	 , #{officeNo}
  	 , #{memNo}
  	 , #{startDate}
  	 , #{endDate}
  	 , default
  	 , default
  	 , default
  	 , #{person}
  	 , #{phone}
  	 , #{bAsk}
  	 , #{total}
  )
