mapper.insert:
insert
  into member
  (
  	   mem_no
     , email
     , mem_pwd
     , mem_name
     , nickname
     , mem_profile
     , mem_check
     , bank
     , account_num
     , enr_date
  )
  values
  (
  	   seq_mno.nextval
  	 , #{email}
  	 , #{memPwd}
  	 , #{memName}
  	 , #{nickName}
  	 , #{memProfile}
  	 , 'L'
  	 , #{bank}
  	 , #{accountNum}
  	 , sysdate
  )

mapper.update:
update
        member


			    set MEM_NAME = #{memName}
			      , NICKNAME = #{nickName}
			      , MEM_PROFILE = #{memProfile}
			      , BANK = #{bank}
			      , ACCOUNT_NUM = #{accountNum}


	      		set MEM_PWD = #{memPwd}
				      , MEM_NAME = #{memName}
				      , NICKNAME = #{nickName}
				      , MEM_PROFILE = #{memProfile}
				      , BANK = #{bank}
				      , ACCOUNT_NUM = #{accountNum}


      		where mem_no = #{memNo}
